package com.design.android.lhj.android_design;import android.content.Context;import com.design.android.lhj.android_design.msgctrl.ControllerRegister;/** * Created by lhj on 18/5/10 */public class AppManager {    private Context mContext;    private AppManager() {    }    private static final class InstanceHolder {        static AppManager appManager = new AppManager();    }    public static AppManager getManagerInstance() {        return InstanceHolder.appManager;    }    public void initialize(Context appContext){        mContext = appContext;        ControllerRegister.initialize();    }}