package com.design.andoid.lhj.android_design;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.TextView;import com.design.andoid.lhj.android_design.msgctrl.MsgDef;import com.design.andoid.lhj.android_design.msgctrl.MsgDispatcher;import com.design.andoid.lhj.android_design.msgctrl.RxBus;import com.design.andoid.lhj.android_design.service.response.UserInfoResult;import com.orhanobut.logger.Logger;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.OnClick;import rx.Subscription;import rx.functions.Action1;public class MainActivity extends AppCompatActivity {    @BindView(R.id.tvContent)    TextView tvContent;    @BindView(R.id.btnGetUserInfo)    Button btnGetUserInfo;    Subscription userInfoDataSubscription;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        ButterKnife.bind(this);        userInfoDataSubscription = RxBus.getInstance().toObservable(UserInfoResult.class)                .subscribe(                        result -> {                            tvContent.setText(result.respData.getUserName());                        }                );    }    @OnClick(R.id.btnGetUserInfo)    public void onViewClicked() {        MsgDispatcher.dispatchMessage(MsgDef.MSG_DEF_GET_USER_INFO);    }    @Override    protected void onDestroy() {        super.onDestroy();        RxBus.getInstance().unsubscribe(userInfoDataSubscription);    }}