package com.design.andoid.lhj.android_design.service;import com.design.andoid.lhj.android_design.R;import com.design.andoid.lhj.android_design.service.response.UserInfoResult;import retrofit2.Call;import retrofit2.http.Field;import retrofit2.http.FormUrlEncoded;import retrofit2.http.GET;import retrofit2.http.POST;import retrofit2.http.Path;import retrofit2.http.Query;import retrofit2.http.Url;/** * */public interface SpaService {    /**     * post请求方式     *     * @param post     * @return     */    @FormUrlEncoded    @POST(RequestConstant.URL_POST_REQUEST)    Call<BaseResult> postRequest(@Field(RequestConstant.KEY_POST) String post);    @GET(RequestConstant.URL_REPLACE_REQUEST)    Call<BaseResult> pathRequest(@Path(RequestConstant.KEY_REPLACE) String path);    @GET    Call<BaseResult> urlRequest(@Url String url);    @GET(RequestConstant.URL_GET_REQUEST)    Call<BaseResult> getRequest(@Query(RequestConstant.KEY_GET) String get);    @GET(RequestConstant.URL_GET_USER_INFO)    Call<UserInfoResult> getUserInfo(@Query(RequestConstant.KEY_TOKEN) String token,                                     @Query(RequestConstant.KEY_SESSION_TYPE) String sessionTypea);}